import React from "react"
import 'bootstrap/dist/css/bootstrap.min.css'
import Table from 'react-bootstrap/Table'
import {compile,derivative} from 'mathjs'
import { Collapse } from "react-bootstrap"
var labelBic=[]

class NewtonRaphson extends React.Component{
    constructor(props){
        super(props)
        this.state={
          SumX:"",
          errorX:"",
          X:"",
          talang:false
        }
        this.setSumX=this.setSumX.bind(this);
        this.setX=this.setX.bind(this);
    }
    setSumX(event){
        this.setState({SumX:event.target.value})
    }
    setX(event){
        this.setState({X:event.target.value})
    }
    runfun(event){
        this.setState({talang:event})
        if(event){
            labelBic.length=0
           this.fun()
        }
        
    }
    showfun(){
        return labelBic
    }
    fun(){
        var x=this.state.X,sum=this.state.SumX,fun="",diff="",dif="",xold=parseFloat(x),scope="",ffun="",fdif="",xnew="",error="",i=0
        while(true){
            fun=compile(sum)
            diff=derivative(sum,"x").evaluate(xold)
            scope={x:parseFloat(xold)}
            ffun=fun.evaluate(scope)
            xnew=xold-(ffun/dif)
            error=((xnew-xold)/xnew)*100
            i++
            xold=xnew
            if(error<1||i>100){
                break
            }
        }
        
    }
    render(){
        const styles={
			width: "250px"
        }
        const styles2={
                width: "200px"
        }
        return(
            <div>
                <label className="alert">
                    <br/><div className="alert alert-danger">NewtonRaphson</div>
                    <br/>
                    <div className="input-group" >
                        <div className="input-group-prepend"><span className="input-group-text">f(x)</span></div>
                            <input type="text" style={styles} value={this.state.SumX} onChange={this.setSumX}/>
                        </div><br/>
                        <div className="input-group">
                            <div className="input-group-prepend"><span className="input-group-text">X</span></div>
                                <input type="number" style={styles} value={this.state.X} onChange={this.setX}/>
                        </div><br/>
                        <button onClick={()=>this.runfun(true)} className="btn btn-danger">sum</ button>
                </label>
                <Collapse in={this.state.talang}><Table>
                    <thead>
                        <tr>
                        <th>Iteration</th>
                        <th>XL</th>
                        <th>XR</th>
                        <th>XM</th>
                        <th>ERROR</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.showfun()}
                    </tbody>
                </Table></Collapse>
            </div>
        )
    }
}
export default NewtonRaphson;
