import React from "react"
import 'bootstrap/dist/css/bootstrap.min.css'
//import Table from 'react-bootstrap/Table'
import { Collapse } from "react-bootstrap"
import {create,all} from'mathjs'
var A = [], B = [], answer = [],answerlu, matrixA = [], matrixB = []
const math = create(all)

class LU extends React.Component{
    constructor(props){
        super(props)
        this.state={
          row:parseInt(0),
          col: parseInt(0),
          talang:false,
          talang2:false
        }
        this.setrow=this.setrow.bind(this)
        this.setcol=this.setcol.bind(this)
    }
    setrow(event){
        this.setState({row:event.target.value})
    }
    setcol(event){
        this.setState({col:event.target.value})
    }
    fun() {
        this.sumMatrix();
        answerlu = math.lusolve(A, B)
        for (var i = 0; i < answerlu.length; i++) {
            answer.push(<h2>X<sub>{i+1}</sub>={Math.round(answerlu[i])}</h2>)
            answer.push(<h2>56565</h2>)
            answer.push(<br />)
        }
        
        
    }
    createMatrix(row, column) {
        for (var i=1 ; i<=row ; i++) {
            for (var j=1 ; j<=column ; j++) {
                matrixA.push(<input id={"a"+i+""+j} key={"a"+i+""+j} placeholder={"a"+i+""+j} />)  
            }
            matrixA.push(<br/>)
            matrixB.push(<input id={"b"+i} key={"b"+i} placeholder={"b"+i} />)
        }   

    }
    sumMatrix() {
        for(var i=0 ; i<this.state.row ; i++) {
            A[i] = []
            for(var j=0 ; j<this.state.col ; j++) {
                A[i][j] = (parseFloat(document.getElementById("a"+(i+1)+""+(j+1)).value));
            }
            B.push(parseFloat(document.getElementById("b"+(i+1)).value));
        }
    }
    runfun2(event){
        this.setState({talang2:event})
        this.setState({talang:false})
        if(event){
            answer=[]
           this.fun()
        }
        
    }
    runfun(event){
        this.setState({talang:event})
        if(event){
            matrixA=[]
            matrixB=[]
           this.createMatrix(this.state.row,this.state.col)
        }
        
    }
    showfun(){
        return answer
    }
    render(){
        const styles={
			width: "250px"
        }
        return(
            <div>
                <label className="alert">
                    <br/><div className="alert alert-danger">CramersRule</div>
                        <div>In linear algebra, Cramer's rule is an explicit formula for the solution of a system of linear equations with as many equations as unknowns, valid whenever the system has a unique solution. It expresses the solution in terms of the determinants of the (square) coefficient matrix and of matrices obtained from it by replacing one column by the column vector of right-hand-sides of the equations. It is named after Gabriel Cramer (1704â€“1752), who published the rule for an arbitrary number of unknowns in 1750,[1][2] although Colin Maclaurin also published special cases of the rule in 1748[3] (and possibly knew of it as early as 1729)</div>
                        <div className="input-group" >
                        <div className="input-group-prepend"><span className="input-group-text">ROW</span></div>
                            <input type="number" style={styles} value={this.state.row} onChange={this.setrow}/>
                        </div><br/>
                        <div className="input-group">
                            <div className="input-group-prepend"><span className="input-group-text">COLUMN</span></div>
                                <input type="number" style={styles} value={this.state.col} onChange={this.setcol}/>
                        </div><br/>
                        <button onClick={()=>this.runfun(true)} className="btn btn-danger">sum</ button>
                    
                </label>
                <Collapse in={this.state.talang}>
                    <div>
                    <div><h2>Matrix [A]</h2><br/>{matrixA}<h2>Vector [B]<br/></h2>{matrixB}</div>
                    <button onClick={()=>this.runfun2(true)} className="btn btn-danger">sum</ button>
                    </div>
                </Collapse>
                <Collapse in={this.state.talang2}>
                    <div>
                        {this.showfun()}
                    </div>
                </Collapse>
            </div>
        )
    }
}
export default LU;
