import React from "react"
import 'bootstrap/dist/css/bootstrap.min.css'
import {compile} from 'mathjs'

class Standard extends React.Component {
    constructor(props){
        super(props)
        this.state={
          SumX:"",
          errorX:"",
          Xl:"",
          Xr:"",
          Xm:"",
          Interation:"",
        }
    }
    fun(event){
        var one =compile(this.state.SumX)
        let xl=parseFloat(this.state.Xl)
        let xr=parseFloat(this.state.Xr)
        let xmold=(xl+xr)/2
        let xmnew=""
        let errorxm=""
        let scopeXr ={x:parseFloat(this.state.Xr)}
        let scopeXm ={x:parseFloat(xmold)}
        var sumxr =one.evaluate(scopeXr)
        var sumxm =one.evaluate(scopeXm)
        var sumvalue=parseFloat(sumxm)*parseFloat(sumxr)
        if(sumvalue<0){
          xl=xmold
        }
        else{
          xr=xmold
        }
        for(var i=0;i<event;i++){
          xmnew=(xl+xr)/2
          scopeXm ={x:parseFloat(xmnew)}
          sumxr =one.evaluate(scopeXr)
          sumxm =one.evaluate(scopeXm)
          sumvalue=parseFloat(sumxm)*parseFloat(sumxr)
          if(sumvalue<0){
            xl=xmnew
          }
          else{
            xr=xmnew
          }
          errorxm=(xmnew-xmold)/xmnew
          xmold=xmnew
        }
    }
    render(){
        const styles={
			width: "250px"
        }
        const styles2={
                width: "200px"
        }
        return(
            <div>
                <label className="alert">
                    <br/><div className="alert alert-danger">Bisection</div>
                    <br/>
                    <div className="input-group" >
                        <div className="input-group-prepend"><span className="input-group-text">f(x)</span></div>
                            <input type="text" style={styles} value={this.state.SumX} onChange={this.handlechange}/>
                        </div><br/>
                        <div className="input-group">
                            <div className="input-group-prepend"><span className="input-group-text">XL</span></div>
                                <input type="number" style={styles} value={this.state.Xl} onChange={this.handlechangeXl}/>
                        </div><br/>
                        <div className="input-group">
                            <div className="input-group-prepend"><span className="input-group-text">XR</span></div>
                                <input type="number" style={styles} value={this.state.Xr} onChange={this.handlechangeXr}/>
                        </div><br/>
                        <div className="input-group" >
                            <div className="input-group-prepend"><span className="input-group-text">Interation</span></div>
                                <input type="number" style={styles2} value={this.state.Interation} onChange={this.handlechangeInteration}/>
                        </div><br/>
                        <button onClick={()=>this.fun(this.state.Interation)} className="btn btn-danger">sum</ button>
                </label>
            </div>
        )
    }
}
export default Standard;
