import React from 'react'
import Table from 'react-bootstrap/Table'
import {compile} from 'mathjs'
//import Bisection from "./Bisection.js"
var dataInTable = []

class BisectionSum extends React.Component {
	constructor(props) {
    super(props)
      this.state = {
        SumX:this.props.SumX,
        errorX:this.props.errorX,
        Xl:this.props.Xl,
        Xr:this.props.Xr,
        Xm:this.props.Xm,          
        Interation:this.props.Interation,
      }
  }
	fun(event){
    var one =compile(this.state.SumX)
    let xl=parseFloat(this.state.Xl)
    let xr=parseFloat(this.state.Xr)
    let xmold=(xl+xr)/2
    let xmnew=""
    let errorxm=""
    var data  = []
    data['xl'] = []
    data['xr'] = []
    data['xm'] = []
    data['error'] = []
    let scopeXr ={x:parseFloat(this.state.Xr)}
    let scopeXm ={x:parseFloat(xmold)}
    var sumxr =one.evaluate(scopeXr)
    var sumxm =one.evaluate(scopeXm)
    var sumvalue=parseFloat(sumxm)*parseFloat(sumxr)
    if(sumvalue<0){
      xl=xmold
    }
    else{
      xr=xmold
    }
    for(var i=0;i<event;i++){
      xmnew=(xl+xr)/2
      scopeXm ={x:parseFloat(xmnew)}
      sumxr =one.evaluate(scopeXr)
      sumxm =one.evaluate(scopeXm)
      sumvalue=parseFloat(sumxm)*parseFloat(sumxr)
      if(sumvalue<0){
        xl=xmnew
      }
      else{
        xr=xmnew
      }
      data['xl'][i+1]  =   xl
      data['xr'][i+1]  =   xr
      data['xm'][i+1]   =   xmnew.toFixed(6) 
      errorxm=(xmnew-xmold)/xmnew
      data['error'][i+1] = errorxm
      xmold=xmnew
    }
    this.createArr(data['xl'], data['xr'], data['xm'], data['error']);
  }
createArr(xl, xr, xm, error) {
    dataInTable = []
    for (var i=0 ; i<xl.length ; i++) {
        dataInTable.push({
            iteration: i+1,
            xl: xl[i],
            xr: xr[i],
            xm: xm[i],
            error: error[i]
        });
    }

}

render() {
	return (
      <div>
          <Table>
            <thead>
                <tr>
                <th>Interation</th>
                <th>XL</th>
                <th>XM</th>
                <th>XR</th>
                <th>ERROR</th>
                </tr>
            </thead>
            <tbody>
                <tr>
                  <td></td>
                  <td>Mark</td>
                  <td>Otto</td>
                  <td>@mdo</td>
                </tr>
            </tbody>
          </Table>
		  </div>
	  )
	}
}

export default BisectionSum;
