import React from "react"
import "./stylecontent.css"
import 'bootstrap/dist/css/bootstrap.min.css';
import BisectionSum from "./BisectionSum.js"
import { Row} from 'react-bootstrap'
import Col from 'react-bootstrap/Col'
import Container from 'react-bootstrap/Container'
import { NumberInput } from 'tailwind-react-ui'


class Bisection extends React.Component{
    constructor(props) {
        super(props)
        this.state = {
          SumX:"",
          errorX:"",
          Xl:"",
          Xr:"",
          Xm:"",
          Interation:"",
          Open:false,

        }
        this.handlechange=this.handlechange.bind(this);
        this.handlechangeXl=this.handlechangeXl.bind(this);
        this.handlechangeXr=this.handlechangeXr.bind(this);
        this.handlechangeInteration=this.handlechangeInteration.bind(this);
    } 
    handlechange(event){
        this.setState({SumX: event.target.value})
    }
    handlechangeXl(event){
        this.setState({Xl: event.target.value})
    }
    handlechangeXr(event){
        this.setState({Xr: event.target.value})
    }
    handlechangeXm(event){
        this.setState({Xm: event.target.value})
    }
    handlechangeInteration(event){
        this.setState({Interation: event.target.value})
    }
    run(event){
        this.setState({Open: event})
    }
    run2(){
        if(this.state.Open===true){
            return <BisectionSum/>
        }
        return <BisectionSum/>
    }
    render(){
        const styles={
			width: "250px"
        }
        const styles2={
                width: "200px"
        }
        return(
            <div>
                <label className="CSSBisectioLabel alert alert-success">
                    <Container>
                        <Row>
                            <Col><label className="alert">
                                <NumberInput name="number" placeholder="Card Number" />
                                <br/><div className="alert alert-danger">Bisection</div>
                                <br/>
                                <div className="input-group" >
                                    <div className="input-group-prepend"><span className="input-group-text">f(x)</span></div>
                                    <input type="text" style={styles} value={this.state.SumX} onChange={this.handlechange}/>
                                </div>
                                <br/>
                                <div className="input-group">
                                    <div className="input-group-prepend"><span className="input-group-text">XL</span></div>
                                    <input type="number" style={styles} value={this.state.Xl} onChange={this.handlechangeXl}/>
                                </div>
                                <br/>
                                <div className="input-group">
                                    <div className="input-group-prepend"><span className="input-group-text">XR</span></div>
                                    <input type="number" style={styles} value={this.state.Xr} onChange={this.handlechangeXr}/>
                                </div>
                                <br/>
                                <div className="input-group" >
                                    <div className="input-group-prepend"><span className="input-group-text">Interation</span></div>
                                    <input type="number" style={styles2} value={this.state.Interation} onChange={this.handlechangeInteration}/>
                                </div>
                                <br/><button onClick={()=>this.run(!this.state.Open)} className="btn btn-danger">sum</ button>
                            </label></Col>
                            <Col><ul><label className="CSSBisectioInput">
                                <div className="card">
                                        {this.run2()}
                                </div>
                            </label></ul></Col>
                        </Row>
                    </Container>
                </label>
            </div>
        )
    }
}
export default Bisection;
