import React from "react"
import 'bootstrap/dist/css/bootstrap.min.css'
import Table from 'react-bootstrap/Table'
import {compile} from 'mathjs'


class Standard extends React.Component {
    constructor(props){
        super(props)
        this.state={
          SumX:"",
          errorX:"",
          Xl:"",
          Xr:"",
          Xm:"",
          Interation:"",
          talang:false
        }
        this.setSumX=this.setSumX.bind(this);
        this.setXl=this.setXl.bind(this);
        this.setXr=this.setXr.bind(this);
        this.setInteration=this.setInteration.bind(this);
    }
    setSumX(event){
        this.setState({SumX:event.target.value})
    }
    setXl(event){
        this.setState({Xl:event.target.value})
    }
    setXr(event){
        this.setState({Xr:event.target.value})
    }
    setInteration(event){
        this.setState({Interation:event.target.value})
    }
    runfun(event){
        if(event){
           this.setState({talang:event}) 
        }
          
    }
    showfun(){
        if(this.state.talang){
            return this.fun(this.state.Interation)   
        }
    }
    fun(event){
        let labelBic=[]
        var one =compile(this.state.SumX)
        let xl=parseFloat(this.state.Xl)
        let xr=parseFloat(this.state.Xr)
        let interation=event
        let xmold=(xl+xr)/2
        let xmnew=""
        let errorxm=""
        var scopeXr ={x:parseFloat(this.state.Xr)}
        var scopeXm ={x:parseFloat(xmold)}
        var sumxr =one.evaluate(scopeXr)
        var sumxm =one.evaluate(scopeXm)
        var sumvalue=parseFloat(sumxm)*parseFloat(sumxr)
        if(sumvalue<0){
          xl=xmold
        }
        else{
          xr=xmold
        }
        labelBic.push(<tr>
                        <td>0</td>
                        <td>{xl}</td>
                        <td>{xr}</td>
                        <td>{xmold}</td>
                        <td>null</td>
                    </tr>)
        for(var i=0;i<interation;i++){
          xmnew=(xl+xr)/2
          scopeXm ={x:parseFloat(xmnew)}
          sumxr =one.evaluate(scopeXr)
          sumxm =one.evaluate(scopeXm)
          sumvalue=parseFloat(sumxm)*parseFloat(sumxr)
          if(sumvalue<0){
            xl=xmnew
          }
          else{
            xr=xmnew
          }
          errorxm=(xmnew-xmold)/xmnew
          labelBic.push(<tr>
                <td>{i+1}</td>
                <td>{xl}</td>
                <td>{xr}</td>
                <td>{xmnew}</td>
                <td>{errorxm}</td>
            </tr>)
          xmold=xmnew
        }
        return labelBic
    }
    render(){
        const styles={
			width: "250px"
        }
        const styles2={
                width: "200px"
        }
        return(
            <div>
                <label className="alert">
                    <br/><div className="alert alert-danger">Bisection</div>
                    <br/>
                    <div className="input-group" >
                        <div className="input-group-prepend"><span className="input-group-text">f(x)</span></div>
                            <input type="text" style={styles} value={this.state.SumX} onChange={this.setSumX}/>
                        </div><br/>
                        <div className="input-group">
                            <div className="input-group-prepend"><span className="input-group-text">XL</span></div>
                                <input type="number" style={styles} value={this.state.Xl} onChange={this.setXl}/>
                        </div><br/>
                        <div className="input-group">
                            <div className="input-group-prepend"><span className="input-group-text">XR</span></div>
                                <input type="number" style={styles} value={this.state.Xr} onChange={this.setXr}/>
                        </div><br/>
                        <div className="input-group" >
                            <div className="input-group-prepend"><span className="input-group-text">Interation</span></div>
                                <input type="number" style={styles2} value={this.state.Interation} onChange={this.setInteration}/>
                        </div><br/>
                        <button onClick={()=>this.runfun(!this.state.talang)} className="btn btn-danger">sum</ button>
                </label>
                <Table>
                    <thead>
                        <tr>
                        <th>Interation</th>
                        <th>XL</th>
                        <th>XR</th>
                        <th>XM</th>
                        <th>ERROR</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.showfun()}
                    </tbody>
                </Table>
            </div>
        )
    }
}
export default Standard;
