import React from "react"
import 'bootstrap/dist/css/bootstrap.min.css'
import Table from 'react-bootstrap/Table'
import {compile} from 'mathjs'
import { Collapse } from "react-bootstrap"
var labelBic=[]

class Graphical extends React.Component{
    constructor(props){
        super(props)
        this.state={
          SumX:"",
          errorX:"",
          start:"",
          finish:"",
          talang:false
        }
        this.setSumX=this.setSumX.bind(this);
        this.setStart=this.setStart.bind(this);
        this.setFinish=this.setFinish.bind(this);
    }
    setSumX(event){
        this.setState({SumX:event.target.value})
    }
    setStart(event){
        this.setState({start:event.target.value})
    }
    setFinish(event){
        this.setState({finish:event.target.value})
    }
    fun(event1,event2){
        let one,scope,sum
        for(var i=parseInt(event1);i<=parseInt(event2);i++){
            one = compile(this.state.Sumx);
            scope = {x:parseFloat(i)}
            sum=one.evaluate(scope);
            labelBic.push(<tr>
                <td>{i}</td>
                <td>{sum}</td>
            </tr>)
        }
    }
    render(){
        const styles={
			width: "250px"
        }
        const styles2={
                width: "200px"
        }
        return(
            <div>
                <label className="alert">
                    <br/><div className="alert alert-danger">Graphical</div>
                    <br/>
                    <div className="input-group" >
                        <div className="input-group-prepend"><span className="input-group-text">f(x)</span></div>
                            <input type="text" style={styles} value={this.state.SumX} onChange={this.setSumX}/>
                        </div><br/>
                        <div className="input-group">
                            <div className="input-group-prepend"><span className="input-group-text">start</span></div>
                                <input type="number" style={styles} value={this.state.start} onChange={this.setStart}/>
                        </div><br/>
                        <div className="input-group">
                            <div className="input-group-prepend"><span className="input-group-text">finish</span></div>
                                <input type="number" style={styles} value={this.state.finish} onChange={this.setFinish}/>
                        </div><br/>
                        <button onClick={()=>this.runfun(true)} className="btn btn-danger">sum</ button>
                </label>
                <Collapse in={this.state.talang}><Table>
                    <thead>
                        <tr>
                        <th>Interation</th>
                        <th>XL</th>
                        <th>XR</th>
                        <th>XM</th>
                        <th>ERROR</th>
                        </tr>
                    </thead>
                    <tbody>
                        
                    </tbody>
                </Table></Collapse>
            </div>
        )
    }
}
export default Graphical;
