import React from "react"
import Head from "./Head.js"
import ChooseMenu from "./ChooseMenu.js"
import 'bootstrap/dist/css/bootstrap.min.css';
import "./style.css"
import { Collapse } from "react-bootstrap";
import Fade from 'react-bootstrap/Fade'

class App extends React.Component {
  constructor(props){
    super(props)
    this.state={
      homemenustart:false,
      homemenu:false,
      wait:""
    }
  }
  tast() {
    setTimeout(function() { //Start the timer
        this.setState({homemenustart: true}) //After 1 second, set render to true
    }.bind(this), this.state.wait)
  }
  sethomemenu(event){
    this.setState({wait:4000})
    this.setState({homemenu:event})

    this.tast();
  }
  space(){
      return
  }
  render() {
    return (
      <body >
        <div className="APPbackground">
          <Head/>    
          <Collapse  in={this.state.homemenu}>
            <div><ChooseMenu/></div>
          </Collapse>        
          <Fade  in={!this.state.homemenustart}>

            <card>
            <div className="center">NUMERIC</div><br/>
              <div className="homemenutop"></div>
              <button 
              onClick={()=>this.sethomemenu(!this.state.homemenustart)}
              className="btn btn-lg btn-outline-success menustartcss"
              >start
            </button>
            <div className="homemenubottom"></div>
            </card>
          </Fade >
        </div>
        <footer className="footer center">
          <div>Made By <a href="ChooseMenu" target="_blank">Title Tawan.</a></div >
        </footer>
      </body>
    )
  }
}

export default App;
