import React from "react"
import Standard from "./Standard/Standard"
import Direct from "./Direct/Direct"
import Integration from "./Integration/Integration"
import Interpolation from "./Interpolation/Interpolation"
import Regression from "./Regression/Regression"
import Container from 'react-bootstrap/Container'
import {Collapse, Row,Card} from "react-bootstrap"


class ChooseMenu extends React.Component {
    constructor(props){
        super(props)
        this.state={
          menu1:false,
          menu2:false,
          menu3:false,
          menu4:false,
          menu5:false,
          menu6:false,
          menuon1:false,
          menuon2:false,
          menuon3:false,
          menuon4:false,
          menuon5:false,
          menuon6:false
        }
        this.setmenuon1=this.setmenuon1.bind(this)
        this.setmenuon2=this.setmenuon2.bind(this)
        this.setmenuon3=this.setmenuon3.bind(this)
        this.setmenuon4=this.setmenuon4.bind(this)
        this.setmenuon5=this.setmenuon5.bind(this)
        this.setmenuon6=this.setmenuon6.bind(this)
        this.setmenuzero1=this.setmenuzero1.bind(this)
        this.setmenuzero2=this.setmenuzero2.bind(this)
        this.setmenuzero3=this.setmenuzero3.bind(this)
        this.setmenuzero4=this.setmenuzero4.bind(this)
        this.setmenuzero5=this.setmenuzero5.bind(this)
        this.setmenuzero6=this.setmenuzero6.bind(this)
    }
    setmenuzero1(event){
        setTimeout(function() {
            this.setState({menuon1:event})
        }.bind(this), 200);
        setTimeout(function() {
            this.setState({menu1:event})
        }.bind(this), 200);  
    }
    setmenuon1(event){
        setTimeout(function() {
            this.setState({menuon1:event})
        }.bind(this), 200);
            this.setState({menu1:event})
    }
    setmenuzero2(event){
        setTimeout(function() {
            this.setState({menuon2:event})
        }.bind(this), 200);
        setTimeout(function() {
            this.setState({menu2:event})
        }.bind(this), 200);  
    }
    setmenuon2(event){
        setTimeout(function() {
            this.setState({menuon2:event})
        }.bind(this), 200);
            this.setState({menu2:event})
    }
    setmenuzero3(event){
        setTimeout(function() {
            this.setState({menuon3:event})
        }.bind(this), 200);
        setTimeout(function() {
            this.setState({menu3:event})
        }.bind(this), 200);  
    }
    setmenuon3(event){
        setTimeout(function() {
            this.setState({menuon3:event})
        }.bind(this), 200);
            this.setState({menu3:event})
    }
    setmenuzero4(event){
        setTimeout(function() {
            this.setState({menuon4:event})
        }.bind(this), 200);
        setTimeout(function() {
            this.setState({menu4:event})
        }.bind(this), 200);  
    }
    setmenuon4(event){
        setTimeout(function() {
            this.setState({menuon4:event})
        }.bind(this), 200);
            this.setState({menu4:event})
    }
    setmenuzero5(event){
        setTimeout(function() {
            this.setState({menuon5:event})
        }.bind(this), 200);
        setTimeout(function() {
            this.setState({menu5:event})
        }.bind(this), 200);  
    }
    setmenuon5(event){
        setTimeout(function() {
            this.setState({menuon5:event})
        }.bind(this), 200);
            this.setState({menu5:event})
    }
    setmenuzero6(event){
        setTimeout(function() {
            this.setState({menuon6:event})
        }.bind(this), 200);
        setTimeout(function() {
            this.setState({menu6:event})
        }.bind(this), 200);  
    }
    setmenuon6(event){
        setTimeout(function() {
            this.setState({menuon6:event})
        }.bind(this), 200);
            this.setState({menu6:event})
    }
    callStandard(){
        return <Standard/>
    }
    callDirect(){
        return <Direct/>
    }
    callIntegration(){
        return <Integration/>
    }
    callInterpolation(){
        return <Interpolation/>
    }
    callRegression(){
        return <Regression/>
    }
    callConjugate(){
        return <Direct/>
    }
    render(){
        const styles={ backgroundColor: '#333', borderColor: '#333'}
        return(
            <div className="CMbackground ">
            <Container >
                    <div>
                    <Container className="ContainerCM">
                        <Card style={styles}>
                        <div className="APPtexthead">
                            Numerical 
                        </div>
                        <div className="Contentnumer">
                            Numerical analysis is the study of algorithms that use numerical approximation (as opposed to symbolic manipulations) for the problems of mathematical analysis (as distinguished from discrete mathematics). Numerical analysis naturally finds application in all fields of engineering and the physical sciences, but in the 21st century also the life sciences, social sciences, medicine, business and even the arts have adopted elements of scientific computations. The growth in computing power has revolutionized the use of realistic mathematical models in science and engineering, and subtle numerical analysis is required to implement these detailed models of the world. For example, ordinary differential equations appear in celestial mechanics (predicting the motions of planets, stars and galaxies); numerical linear algebra is important for data analysis; stochastic differential equations and Markov chains are essential in simulating living cells for medicine and biology.
                        </div>
                        <div className="STspacetop"/>
                        <div className="Contentnumerhead" id="Roots of Equations">
                            Roots of Equations 
                        </div>
                        <div className="Contentnumer">
                            Roots (or Zeros) of a function f(x) are values of x that produces an output of 0. Roots can be real or complex numbers. Finding the root of [\displaystyle f(x)-g(x)][\displaystyle f(x)-g(x)] is the same as solving the equation [\displaystyle f(x)=g(x)]f(x)=g(x). Solving an equation is finding the values that satisfy the condition specified by the equation.
                            Lower degree (quadratic, cubic, and quartic) polynomials have closed-form solutions, but numerical methods may be easier to use. To solve a quadratic equation we can use the quadratic formula.
                        </div>
                        <a href="https://en.wikibooks.org/wiki/Introduction_to_Numerical_Methods/Roots_of_Equations">credit</a>  
                        <Row className="ContainerCM">     
                            <Collapse in={!this.state.menuon1}>
                            <Card className="cardCM"><div className="card shadow-sm">
                                <div className="card card-title center">
                                    <div className="STspacetop"/>
                                        <h5>Roots of Equations</h5>
                                    <div className=" STspacebottom"/>
                                </div>
                                <Card className="cardCM2"><ul>
                                    <li>Graphical</li>
                                    <li>Bisection</li>
                                    <li>FlasePosition</li>
                                    <li>OnePoint</li>
                                    <li>NewtonRaphson</li>
                                    <li>Secant</li>
                                </ul></Card>
                                <div className="card card-body">
                                    <button className="btn btn-outline-outline-secondary" onClick={()=>this.setmenuon1(!this.state.menuon1)}>View</button>
                                </div>
                            </div></Card></Collapse>
                        </Row>
                        <Collapse in={this.state.menu1}>
                                <div >
                                    {this.state.menuon1 && this.callStandard()}
                                    <button href="#Roots of Equations" onClick={()=>this.setmenuzero1(!this.state.menu1)} className="btn btn-primary">BackA</button>                                                            
                                    <div className="CMspacebutton"/>
                                </div>
                        </Collapse>
                        <Row className="ContainerCM">
                            <Collapse in={!this.state.menuon2}>
                            <Card className="cardCM"><div className="card shadow-sm">
                            <div className="card card-title center">
                                    <div className="STspacetop"/>
                                        <h5>Direct Methods</h5>
                                    <div className=" STspacebottom"/>
                                </div>
                                <Card className="cardCM2"><ul>
                                    <li>Cramer's Rule</li>
                                    <li>Guess Elimination</li>
                                    <li>Guess Jurdan</li>
                                    <li>Lu Decomposition</li>
                                    <li>Cholesky Decomposition</li>
                                </ul></Card>
                                <div className="card card-body">
                                    <button className="btn btn-outline-outline-secondary" onClick={()=>this.setmenuon2(!this.state.menuon2)}>View</button>
                                </div>
                            </div></Card></Collapse>
                            
                        </Row>
                        <Collapse in={this.state.menu2}>
                                <div className="ContainerCMA">
                                    {this.state.menuon2 && this.callDirect()}
                                    <button onClick={()=>this.setmenuzero2(!this.state.menu2)} className="btn btn-primary">Back</button>
                                    <div className="CMspacebutton"/>
                                </div>
                        </Collapse>
                        <Row className="ContainerCM">
                            <Collapse in={!this.state.menuon3}>
                            <Card className="cardCM"><div className="card shadow-sm">
                            <div className="card card-title center">
                                    <div className="STspacetop"/>
                                        <h5>Integration</h5>
                                    <div className=" STspacebottom"/>
                                </div>
                                <Card className="cardCM2"><ul>
                                    <li>Trapezidal</li>
                                    <li>Simpsom</li>
                                </ul></Card>
                                <div className="card card-body">
                                    <button className="btn btn-outline-outline-secondary" onClick={()=>this.setmenuon3(!this.state.menuon3)}>View</button>
                                </div>
                            </div></Card></Collapse>
                            
                        </Row>
                        <Collapse in={this.state.menu3}>
                            <div className="ContainerCMA">
                                {this.state.menuon3 && this.callIntegration()}
                                <button onClick={()=>this.setmenuzero3(!this.state.menu3)} className="btn btn-primary">Back</button>
                            <div className="CMspacebutton"/>
                            </div>
                        </Collapse> 
                        <Row className="ContainerCM">
                            <Collapse in={!this.state.menuon4}>
                            <Card className="cardCM"><div className="card shadow-sm">
                            <div className="card card-title center">
                                    <div className="STspacetop"/>
                                        <h5>Interpolation</h5>
                                    <div className=" STspacebottom"/>
                                </div>
                                <Card className="cardCM2"><ul>
                                    <li>Newton's divided-difference</li>
                                    <li>Lagrange polynomials</li>
                                    <li>Spline interpolation</li>
                                </ul></Card>
                                <div className="card card-body">
                                    <button className="btn btn-outline-outline-secondary" onClick={()=>this.setmenuon4(!this.state.menuon4)}>View</button>
                                </div>
                            </div></Card></Collapse>
                            
                        </Row>
                        <Collapse in={this.state.menu4}>
                            <div className="ContainerCMA">
                                {this.state.menuon4 && this.callInterpolation()}
                                <button onClick={()=>this.setmenuzero4(!this.state.menu4)} className="btn btn-primary">Back</button>
                                <div className="CMspacebutton"/>
                            </div>
                        </Collapse>
                        <Row className="ContainerCM">
                            <Collapse in={!this.state.menuon5}>
                            <Card className="cardCM"><div className="card shadow-sm">
                            <div className="card card-title center">
                                    <div className="STspacetop"/>
                                        <h5>Regression</h5>
                                    <div className=" STspacebottom"/>
                                </div>
                                <Card className="cardCM2"><ul>
                                    <li>Least-Square Regression</li>
                                    <li>Linear Regression</li>
                                    <li>Multiple linear Regression</li>
                                    <li>Polynomial Regression</li>
                                </ul></Card>
                                <div className="card card-body">
                                    <button className="btn btn-outline-outline-secondary" onClick={()=>this.setmenuon5(!this.state.menuon5)}>View</button>
                                </div>
                            </div></Card></Collapse>
                            
                        </Row>
                            <Collapse in={this.state.menu5}>
                                <div className="ContainerCMA">
                                    {this.state.menuon5 && this.callRegression()}
                                    <button onClick={()=>this.setmenuzero5(!this.state.menu5)} className="btn btn-primary">Back</button>
                                    <div className="CMspacebutton"/>
                                </div>
                            </Collapse>
                        <Row className="ContainerCM">
                            <Collapse in={!this.state.menuon6}>
                            <Card className="cardCM"><div className="card shadow-sm">
                            <div className="card card-title center">
                                    <div className="STspacetop"/>
                                        <h5>Conjugate</h5>
                                    <div className=" STspacebottom"/>
                                </div>
                                <Card className="cardCM2"><ul>
                                    <li>Conjugate Gradient</li>
                                </ul></Card>
                                <div className="card card-body">
                                    <button className="btn btn-outline-outline-secondary" onClick={()=>this.setmenuon6(!this.state.menuon6)}>View</button>
                                </div>
                            </div></Card></Collapse>
                            
                        </Row>
                            <Collapse in={this.state.menu6}>
                                <div className="ContainerCMA">
                                    {this.state.menuon6 && this.callStandard()}
                                    <button onClick={()=>this.setmenuzero6(!this.state.menu6)} className="btn btn-primary">Back</button>
                                    <div className="CMspacebutton"/>
                                </div>
                            </Collapse><div className="CMspacebutton"/>
                    </Card><br/></Container>
                    </div>
                </Container> </div>
        )
    }
}
export default ChooseMenu;
